# 因為 csv 裡面除了英文字元有些被轉成中文亂碼 所以使用 latin-1 編碼
data = pd.read_csv(path,encoding='latin-1', low_memory=False)

# data = pd.read_csv('D:/qiongyun/desktop/scopus/STM_Model/data.csv',encoding='utf-8')

# 取 Abstract_lemma column 的資料
dataa = data['Abstract_lemma']
df = dataa.copy()
# 套用 spacy
nlp = spacy.load("en_core_web_lg")
array = []
for i in range(len(dataa)):
    for j in country:
        # 找字串中是否含有 country 內的字串
        if j.lower() in str(dataa[i]).lower():
            df[i] = str(dataa[i]).lower().replace(j.lower(), "")
    # csv 檔案內的字串必須符合正規表示式
    array.append(str(df[i]).translate(str.maketrans('', '', string.punctuation)))

a = array

token_list = []
# 找字串中的名詞和形容詞
for x in a:
    r = nlp(x)
    arr = ''
    for tx in r:
        if tx.pos_ == "NOUN" or tx.pos_ == "ADJ":
            arr = arr + str(tx) + " "
    token_list.append([arr])
token_list = np.array(token_list)

data['clean_data'] = token_list
data.head()
data.to_csv('D:/qiongyun/desktop/test.csv')

